import 'package:flutter/material.dart';

class Assignment3 extends StatefulWidget {
  const Assignment3({super.key});
  @override
  State<Assignment3> createState() => _Assignment3State();
}

class _Assignment3State extends State<Assignment3> {
  int? selectedIndex = 0;

  final List<String> imageList = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
  ];

  void showNextImg() {
    setState(() {
      if (selectedIndex == imageList.length - 1) selectedIndex = 0;
      selectedIndex = selectedIndex! + 1;
    });
  }

  void showFirstImg() {
    setState(() {
      if (selectedIndex == imageList.length - 1) selectedIndex = 0;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("Display Dog Images"),
        backgroundColor: Colors.deepPurple,
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Image.network(
              imageList[selectedIndex!],
              width: 300,
              height: 300,
            ),
            const SizedBox(
              height: 20,
            ),
            ElevatedButton(
              onPressed: showNextImg,
              child: const Text("Next"),
            ),
            const SizedBox(
              height: 20,
            ),
            ElevatedButton(
                onPressed: () {
                  setState(() {
                    selectedIndex = 0;
                  });
                },
                child: const Text("Reset")),
            const SizedBox(
              height: 20,
            ),
            ElevatedButton(
                onPressed: showFirstImg, child: const Text("First Image")),
          ],
        ),
      ),
    );
  }
}
